mapper.select:
  	select
    
    from patient
    
    	
    		and patient_name like CONCAT('%',#{patientName, jdbcType=VARCHAR},'%')
    	
    	
    		and patient_sex = #{patientSex, jdbcType=VARCHAR}
    	
    	
    		and patient_phone like CONCAT('%',#{patientPhone, jdbcType=VARCHAR},'%')
    	



    	
    		and TIMESTAMPDIFF(YEAR, patient_birth, CURDATE()) >= #{patientMinAge, jdbcType=INTEGER}
    	
    	
    		and TIMESTAMPDIFF(YEAR, patient_birth, CURDATE()) <= #{patientMaxAge, jdbcType=INTEGER}
